// Login.js
import React, { useState } from 'react';
import axios from 'axios';

// Configure axios defaults
axios.defaults.withCredentials = true;

const Login = ({ onLogin, onOTPRequired }) => {
  const [clientId, setClientId] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      console.log('Authenticating client:', clientId);

      const response = await axios.post('http://localhost:5005/api/login', {
        client_id: clientId
      });

      // Handle authentication response
      if (response.data.status === 'SUCCESS') {
        // Store client_id in localStorage for persistence across page refreshes
        localStorage.setItem('clientId', clientId);

        if (response.data.needOTP) {
          // If OTP is needed, redirect to OTP verification
          onOTPRequired(clientId);
        } else {
          // If already authenticated, proceed to success
          onLogin(response.data);
        }
      } else {
        setError(response.data.message || 'Authentication failed');
      }
    } catch (error) {
      console.error('Error during login:', error);

      if (error.response) {
        setError(error.response.data.message || 'Server error during authentication');
      } else if (error.request) {
        setError('Could not connect to server. Please check if the server is running.');
      } else {
        setError('Error during authentication: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h3 className="text-center">MOFSL Authentication</h3>
            </div>
            <div className="card-body">
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error}
                </div>
              )}
              <form onSubmit={handleSubmit}>
                <div className="form-group mb-3">
                  <label htmlFor="clientId">Client ID</label>
                  <input
                    type="text"
                    className="form-control"
                    id="clientId"
                    value={clientId}
                    onChange={(e) => setClientId(e.target.value)}
                    placeholder="Enter your client ID"
                    disabled={loading}
                    required
                  />
                </div>
                <div className="d-grid">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading || !clientId}
                  >
                    {loading ? 'Authenticating...' : 'Login'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
