// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Login from './components/Login';
import Register from './components/Register';
import OTPVerification from './components/OTPVerification';
import Dashboard from './components/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import Navbar from './components/Navbar';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(
    localStorage.getItem('isAuthenticated') === 'true'
  );

  // Store the clientId in state so it's available across the app
  const [clientId, setClientId] = useState(
    localStorage.getItem('clientId') || ''
  );

  // Use effect to update client ID when localStorage changes
  useEffect(() => {
    const storedClientId = localStorage.getItem('clientId');
    if (storedClientId) {
      setClientId(storedClientId);
    }
  }, []);

  const handleLogin = (data) => {
    setIsAuthenticated(true);
    localStorage.setItem('isAuthenticated', 'true');

    // If login data includes a client_id, store it
    if (data && data.client_id) {
      setClientId(data.client_id);
      localStorage.setItem('clientId', data.client_id);
    }
  };

  // This function will be passed as onSuccess to OTPVerification
  const handleOTPSuccess = (data) => {
    setIsAuthenticated(true);
    localStorage.setItem('isAuthenticated', 'true');

    // Navigate to dashboard
    window.location.href = '/dashboard';
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('clientId');
    setClientId('');
  };

  return (
    <Router>
      <div className="App">
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />
        <div className="container mt-4">
          <Routes>
            <Route
              path="/"
              element={
                isAuthenticated ? (
                  <Navigate to="/dashboard" />
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route
              path="/login"
              element={
                isAuthenticated ? (
                  <Navigate to="/dashboard" />
                ) : (
                  <Login onLogin={handleLogin} />
                )
              }
            />
            <Route
              path="/register"
              element={
                isAuthenticated ? (
                  <Navigate to="/dashboard" />
                ) : (
                  <Register />
                )
              }
            />
            <Route
              path="/verify-otp"
              element={
                <OTPVerification 
                  clientId={clientId}
                  onSuccess={handleOTPSuccess}  // Match the prop name expected in OTPVerification
                  onBackToLogin={() => window.location.href = '/login'}
                />
              }
            />
            <Route
              path="/dashboard"
              element={
                <PrivateRoute isAuthenticated={isAuthenticated}>
                  <Dashboard />
                </PrivateRoute>
              }
            />
          </Routes>
        </div>
        <ToastContainer position="bottom-right" />
      </div>
    </Router>
  );
}

export default App;
