// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5005/api';



const Login = ({ onLogin }) => {
    const [clientId, setClientId] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
      e.preventDefault();

      if (!clientId) {
        toast.error('Please enter your Client ID');
        return;
      }

      setLoading(true);

      try {
        const response = await axios.post(
          `${API_URL}/login`,
          { client_id: clientId, password },
          { withCredentials: true }
        );

        localStorage.setItem('clientId', clientId);

        if (response.data.needOTP) {
          navigate('/verify-otp');
          toast.info('Please enter the OTP sent to your registered mobile/email');
        } else {
          onLogin();
          toast.success('Login successful!');
          navigate('/dashboard');
        }
      } catch (error) {
        const message = error.response?.data?.message || 'Login failed. Please try again.';
        toast.error(message);
      } finally {
        setLoading(false);
      }
    };

    return (
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h4 className="text-center">Client Login</h4>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label htmlFor="clientId" className="form-label">
                    Client ID
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="clientId"
                    value={clientId}
                    onChange={(e) => setClientId(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label htmlFor="password" className="form-label">
                    Password (if required)
                  </label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <small className="form-text text-muted">
                    Enter the password you provided during registration, if any
                  </small>
                </div>
                <div className="d-grid gap-2">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading}
                  >
                    {loading ? (
                      <span>
                        <span className="spinner-border spinner-border-sm me-2" />
                        Logging in...
                      </span>
                    ) : (
                      'Login'
                    )}
                  </button>
                </div>
              </form>
              <div className="mt-3 text-center">
                <p>
                  Don't have an account?{' '}
                  <a href="/register">Register here</a>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  export default Login;
