// src/components/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5005/api';

const Login = ({ onLogin }) => {
  const [clientId, setClientId] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!clientId) {
      toast.error('Please enter your Client ID');
      return;
    }

    setLoading(true);

    try {
      const response = await axios.post(
        `${API_URL}/login`,
        { client_id: clientId },
        { withCredentials: true }
      );

      localStorage.setItem('clientId', clientId);

      if (response.data.needOTP) {
        navigate('/verify-otp');
        toast.info('Please enter the OTP sent to your registered mobile/email');
      } else {
        onLogin();
        toast.success('Authentication successful!');
        navigate('/dashboard');
      }
    } catch (error) {
      const message = error.response?.data?.message || 'Authentication failed. Please try again.';
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <div className="card">
          <div className="card-header">
            <h4 className="text-center">Generate OTP for Authentication</h4>
          </div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="clientId" className="form-label">
                  Client ID
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="clientId"
                  value={clientId}
                  onChange={(e) => setClientId(e.target.value)}
                  required
                />
                <small className="form-text text-muted">
                  Enter your Client ID to receive an OTP for authentication
                </small>
              </div>
              <div className="d-grid gap-2">
                <button
                  type="submit"
                  className="btn btn-primary"
                  disabled={loading}
                >
                  {loading ? (
                    <span>
                      <span className="spinner-border spinner-border-sm me-2" />
                      Authenticating...
                    </span>
                  ) : (
                    'Authenticate'
                  )}
                </button>
              </div>
            </form>
            <div className="mt-3 text-center">
              <p>
                Don't Have Your Account Information Saved With Us?{' '}
                <a href="/register">Save it Here First</a>
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
