// OTPVerification.js
import React, { useState } from 'react';
import axios from 'axios';

// Configure axios defaults
axios.defaults.withCredentials = true;  // Always include credentials

const OTPVerification = ({ clientId, onSuccess, onBackToLogin }) => {
  const [otp, setOtp] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Include client_id in the request body
      const response = await axios.post('http://localhost:5000/api/verify-otp', {
        otp,
        client_id: clientId
      });

      if (response.data.status === 'SUCCESS') {
        onSuccess(response.data);
      } else {
        setError(response.data.message || 'Verification failed');
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);

      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        setError(error.response.data.message || 'Server error during verification');
      } else if (error.request) {
        // The request was made but no response was received
        setError('Could not connect to server. Please check if the server is running.');
      } else {
        // Something happened in setting up the request that triggered an Error
        setError('Error during verification: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleResendOTP = async () => {
    setLoading(true);
    setError('');

    try {
      // Include client_id in the request body
      const response = await axios.post('http://localhost:5000/api/resend-otp', {
        client_id: clientId
      });

      if (response.data.status === 'SUCCESS') {
        alert('OTP resent successfully!');
      } else {
        setError(response.data.message || 'Failed to resend OTP');
      }
    } catch (error) {
      console.error('Error resending OTP:', error);

      if (error.response) {
        setError(error.response.data.message || 'Server error while resending OTP');
      } else if (error.request) {
        setError('Could not connect to server. Please check if the server is running.');
      } else {
        setError('Error resending OTP: ' + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header">
              <h3 className="text-center">OTP Verification</h3>
            </div>
            <div className="card-body">
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error}
                </div>
              )}
              <form onSubmit={handleSubmit}>
                <div className="form-group mb-3">
                  <label htmlFor="otp">Enter OTP sent to your registered mobile/email</label>
                  <input
                    type="text"
                    className="form-control"
                    id="otp"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter OTP"
                    disabled={loading}
                    required
                  />
                </div>
                <div className="d-grid gap-2">
                  <button
                    type="submit"
                    className="btn btn-primary"
                    disabled={loading || !otp}
                  >
                    {loading ? 'Verifying...' : 'Verify OTP'}
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={handleResendOTP}
                    disabled={loading}
                  >
                    Resend OTP
                  </button>
                  {onBackToLogin && (
                    <button
                      type="button"
                      className="btn btn-link"
                      onClick={onBackToLogin}
                      disabled={loading}
                    >
                      Back to Login
                    </button>
                  )}
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OTPVerification;
